export default function Home(props) {
  const theme = useTheme();
  const [open, setOpen] = React.useState(false);
  const [opent, setOpent] = React.useState(false);
  const [page_to_display, setPageToDisplay] = React.useState('Women');
  const [isLoaded, setIsLoaded] = React.useState(false);

  const [qty, setQty] = useState(0);
  var dispatch = useDispatch();

  const [categoryList, setCategoryList] = useState([]);
  const [all_products, setAllProducts] = useState([]);

  let navigate = useNavigate();
  const classes = useStyles();


  const navigateto =()=>{
    navigate('/dashboard')
  }

  const navigatetoProceed =()=>{
    navigate('/signin')
  }

  const handleDrawerToggle = () => {
    setOpen(!open);
  };

  const toggleSlider = () => {
    setOpent(!opent);
  };

  const fetchAllItem = async () => {
    var result = await getData("getProductbyId");
    console.log("RESULT", result);
    setCategoryList(Object.keys(result));
    setAllProducts(result);
    setIsLoaded(true);
  };

  useEffect(function () {
      fetchAllItem();


  }, []);

  var cart = useSelector(state => state.cart)
  console.log("CARTT-->", cart);
  var cartitems = Object.values(cart);


  var totalamount = cartitems.reduce((a, b) => getTotalAmount(a, b), 0)
  function getTotalAmount(p1, p2) {
    var price = p2.offerprice > 0 ? p2.offerprice * p2.qty : p2.price * p2.qty
    return (price + p1)
  }


  const sideList = () => (
    <Box sx={{width:drawerWidthtwo}} className={classes.menuSliderContainer} component="div">
      <List>
      {cartitems.map((item, index) => (
        <ListItem button >
          <ListItemIcon>
           <img src={item.image} style={{width:80,borderRadius:10}} />
          </ListItemIcon>
          <div style={{display:'flex',flexDirection:'column',padding:5}}>
          <ListItemText primary={item.productname}  style={{fontWeight:'bold'}} />    
          <ListItemText primary= {item.offerprice > 0 ? (
                <div style={{width:280, fontSize: 18, fontWeight: "500", letterSpacing: 1 }}>
                  <s
                    style={{
                      color: "#353b48",
                      fontSize: 14,
                      fontWeight: "500",
                      letterSpacing: 1,
                    }}
                  >
                    &#8377; {item.price}  
                  </s>{" "}


                  &#8377;{item.offerprice} x {item.qty}

                </div>
              ) : (
                <>
                  <div
                    style={{
                      width:280,
                      fontSize: 18,
                      fontWeight: "bold",
                      letterSpacing: 2,
                    }}
                  >
                    &#8377;{item.price} x {item.qty}
                  </div>
                  <div
                    style={{
                      display:'flex',
                      color: "darkgreen",
                      fontSize: 18,
                      fontWeight: "500",
                      letterSpacing: 1,
                    }}
                  >
                    &nbsp;
                    <span style={{marginLeft:'auto'}}>
                   &#8377;{item.price * item.qty}
                   </span>
                   <Avatar onClick={() => handleQtyChange(item, -1)} sx={{ bgcolor: 'grey', color: 'black', fontSize: 15, fontWeight: 'bold', margin: 3 }} variant="square">
            -
          </Avatar>
          <div sx={{ display: 'flex', fontSize: 15, fontWeight: 'bold' }}>{item.qty}</div>

                   <Avatar onClick={() => handleQtyChange(item, +1)} sx={{ bgcolor: 'grey', color: 'black', fontSize: 15, fontWeight: 'bold', margin: 3 }} variant="square">
            +
          </Avatar>

                  </div>


                </>
              )}  />

           </div>


        </ListItem>
      ))}
    </List>

    <Divider />

  <div style={{padding:10,display:'flex',alignItems:'center',width:380}}>
    <span style={{fontWeight:'bold',fontSize:18}}>Net Amount:</span>
    <span style={{fontWeight:'bold',fontSize:18,marginLeft:'auto'}}>&#8377;{totalamount}
</span> 
  </div>

  <div style={{padding:10,display:'flex',alignItems:'center',width:380}}>
    <Button  onClick={()=>navigatetoProceed()} variant="contained" fullWidth style={{background:'black',color:'#FFF',fontWeight:'bold',fontSize:18}}>Proceed</Button>
  </div>
    </Box>
  );

  const showCategories = () => {
    return categoryList.map((item) => {
      return (
        <div style={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center' }}>
          <br /> <Button onClick={() => setPageToDisplay(item)} fullWidth style={{ textAlign: 'center', fontSize: 18, fontWeight: 'bold' }}>{item}</Button>
          <br />
        </div>
      )
    })
  }

  const handleQtyChange = (ele, op) => {
    let cart_items = [...cartitems]
    let index = cart_items.findIndex(element => element.productid === ele.productid);
    if(index > -1)
    {
      cart_items[index].qty =  cart_items[index].qty + (op?op:1);
      if(cart_items[index].qty < 1)
        cart_items.splice(index, 1);
    }
    else
      cart_items.push(ele);

    dispatch({ type: "UPDATE_ITEM", payload: cart_items});
  }

  // const handleQtyChange1 = (item, value) => {
  //   if (value > 0) {
  //     item["qty"] = value;
  //     setQty(value);
  //     dispatch({ type: "ADD_ITEM", payload: [item.productid, item] });
  //   } else {
  //     dispatch({ type: "REMOVE_ITEM", payload: [item.productid, item] });
  //   }
  // };

  const showProduct = (items) => {
    return items.map((item) => {
      return (
        <div >
          <Card sx={{ maxWidth: 250 }}>
            <CardHeader style={{ textAlign: 'center' }}
              title={item.productName} />
            {/* <img src={item.image}/> */}
            <CardMedia
              component="img"
              height="194"
              image={item.image}

            />
            <Typography style={{ textAlign: 'center', marginTop: 10 }}>Price : {item.price}</Typography>
            <Typography style={{ textAlign: 'center', marginTop: 10 }}> Description : {item.productDescription}</Typography>
            <CardActions style={{ justifyContent: 'center' }} disableSpacing>
              <ShoppingCartComponent
                {...item}
                handleUpdate={() => handleQtyChange(item)}
              />
            </CardActions>
          </Card>
          <br />
          <br />
        </div>
      )
    })
  }

  if(!isLoaded)
    return (<div>Loading...</div>)

  return (
    <Box sx={{ display: 'flex' }}>
      <CssBaseline />
      <AppBar position="fixed" open={open}>
        <Toolbar>
          <IconButton
            color="inherit"
            aria-label="open drawer"
            onClick={handleDrawerToggle}
            edge="start"
            sx={{ mr: 2, ...(open && { display: 'none' }) }}
          >
            <MenuIcon />
          </IconButton>
          <Typography variant="h6" noWrap component="div">
            List of Products
          </Typography>

          <Button style={{
            position: 'absolute',
            left: '80%',
            bottom: '20%',
            color: 'white'
          }}
            onClick={navigateto}
          >
            Admin Pannel
          </Button>
<Badge badgeContent={qty} >
          <ShoppingCartSharpIcon style={{
            position: 'absolute',
            left: '95%',
            bottom: '30%'
          }} onClick={toggleSlider}
          >

          </ShoppingCartSharpIcon>
          </Badge>
          <Drawer open={opent} anchor="right" onClose={toggleSlider}>
            {sideList()}
          </Drawer>
        </Toolbar>
      </AppBar>
      <Drawer
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          '& .MuiDrawer-paper': {
            width: drawerWidth,
            boxSizing: 'border-box',
          },
        }}
        variant="persistent"
        anchor="left"
        open={open}
      >
        <DrawerHeader>
          <IconButton onClick={handleDrawerToggle}>
            {theme.direction === 'ltr' ? <ChevronLeftIcon /> : <ChevronRightIcon />}
          </IconButton>
        </DrawerHeader>
        {/* <Divider /> */}

        <List>
          {showCategories()}
          <Divider />
        </List>

      </Drawer>
      <Main open={open}>
        <DrawerHeader />
        {showProduct([...all_products[page_to_display]])}
      </Main>
    </Box>
  );
}
