import React, { useEffect, useState, createRef } from "react";
import Header from "./Header";
import { getData, ServerURL } from "../FetchNodeServices"
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick"
import Paper from '@mui/material/Paper';
import { makeStyles } from '@mui/styles';
import Footer from "./Footer";
import { ArrowBackIos, ArrowForwardIos } from "@mui/icons-material";


const useStyles = makeStyles({
  root: {
    background: '#ecf0f1'
  },
})

// setting for banner slider
var settings = {
  dots: false,
  infinite: true,
  speed: 1000,
  autoplay: true,
  slidesToShow: 1,
  slidesToScroll: 1,
  autoplaySpeed: 3000
};

// setting for Category slider
var Categorysettings = {
  dots: false,
  infinite: true,
  speed: 1000,
  arrows: false,
  autoplay: false,
  slidesToShow: 6,
  slidesToScroll: 1,
  autoplaySpeed: 3000
};




export default function Home(props) {
  console.log("HOME", props)

  // for category slider
  var cSlider = createRef();



  const classes = useStyles();
  const [categoryList, setCategoryList] = useState([])




  const fetchAllCategory = async () => {
    var result = await getData("getCategory");
    setCategoryList(result.data);
  };




  //category 
  const showCategories = () => {
    return categoryList.map((item) => {
      return (<div style={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center' }}>
        <div style={{ padding: 5, display: 'flex', justifyContent: 'center', alignItems: 'center', width: 170, height: 170, borderRadius: '50%', background: '#fff', margin: 10 }}>
          <img   alt="category" src={`${ServerURL}/images/${item.icon}`} style={{ width: 120, Height: 120 }} />

        </div>
        <div style={{ textAlign: 'center', fontSize: 18, fontWeight: 'bold' }}>{item.categoryname}</div>

      </div>
      )
    })


  }




  // new arrival
  const showNewArrival = () => {
    return newArrivalList.map((item) => {
      return (
        <div style={{ fontSize: 36, fontWeight: 'bold', letterSpacing: 1, display: 'flex', justifyContent: 'center', alignItems: 'center', flexDirection: 'column', padding: 10 }}>
          <Paper style={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center', width: 320, height: 320, background: '#fff', margin: 10, padding: 5 }} elevation={2} >

            <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>

              <img  alt="category"  src={`${ServerURL}/images/${item.picture}`} style={{ width: 170, Height: 170 }} />
            </div>

            <div style={{ fontSize: 24, fontWeight: '400', letterSpacing: 2 }}>{item.productname}</div>

{item.offerprice > 0 ? <div style={{ fontSize: 28, fontWeight: '500', letterSpacing: 2 }}>&#8377;{item.offerprice}
  <s style={{ color: '#353b48', fontSize: 14, fontWeight: '500', letterSpacing: 2 }}>&#8377; {item.price}
  </s>

</div>
  : <div style={{ fontSize: 28, fontWeight: 'bold', letterSpacing: 2 }}>&#8377;{item.price}
  </div>
}

          </Paper>
           </div>
      )
    })


  }


  useEffect(function () {

    fetchAllBanners()
    fetchAllCategory()
    fetchAllSubCategory()
    fetchAllTrending()
    fetchAllNewArrival()

  }, [])

  const handleBack = () => {
    cSlider.current.slickPrev()
  }
  const handleForward = () => {
    cSlider.current.slickNext()
  }


  return (

    <div className={classes.root}>
      <Header history={props.history} />
      <div style={{marginTop:50}}>
        <Slider {...settings}>

          {showSlider()}
        </Slider>
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', flexDirection: 'row' }}>
          <ArrowBackIos onClick={() => handleBack()} />
          <div style={{ width: 1300, padding: 10 }}>

            <Slider ref={cSlider}{...Categorysettings}>

              {showCategories()}

            </Slider>

          </div>
          <ArrowForwardIos onClick={() => handleForward()} />
        </div>
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', flexDirection: 'row', padding: 10, flexWrap: 'wrap' }}>
          {showSubCategories()}
        </div>

        <div style={{ fontSize: 36, fontWeight: 'bold', letterSpacing: 1, display: 'flex', justifyContent: 'center', alignItems: 'center', flexDirection: 'row', padding: 10 }}>
          Trending Products
        </div>


        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', flexDirection: 'row', padding: 10, flexWrap: 'wrap' }}>
          {showTrending()}
        </div>

        <div style={{ fontSize: 36, fontWeight: 'bold', letterSpacing: 1, display: 'flex', justifyContent: 'center', alignItems: 'center', flexDirection: 'row', padding: 10 }}>
          New Arrival Products
        </div>


        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', flexDirection: 'row', padding: 10, flexWrap: 'wrap' }}>
          {showNewArrival()}
        </div>

        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', flexDirection: 'row', padding: 10, flexWrap: 'wrap' }}>
          <div style={{
            textAlign: 'center', background: '#000', color: '#FFF', padding: "10px 20px", border: "none", textDecoration: 'none',
            display: "inline-block",
            margin: "4px 2px",
            cursor: "pointer",
            borderRadius: "18px",
            fontWeight: 600

          }}>
            {"View All >"}
          </div>
        </div>


        <Footer />
      </div>

    </div>)


}
