import React, { useEffect, useState, createRef } from "react";
import { getData, ServerURL } from "../FetchNodeServices"
// import "slick-carousel/slick/slick.css";
// import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick"
import Paper from '@mui/material/Paper';
import { makeStyles } from '@mui/styles';
import { ArrowBackIos, ArrowForwardIos } from "@mui/icons-material";


const useStyles = makeStyles({
  root: {
    background: '#ecf0f1'
  },
})

// setting for banner slider
var settings = {
  dots: false,
  infinite: true,
  speed: 1000,
  autoplay: true,
  slidesToShow: 1,
  slidesToScroll: 1,
  autoplaySpeed: 3000
};

// setting for Category slider
var Categorysettings = {
  dots: false,
  infinite: true,
  speed: 1000,
  arrows: false,
  autoplay: false,
  slidesToShow: 6,
  slidesToScroll: 1,
  autoplaySpeed: 3000
};




export default function Home(props) {
  console.log("HOME", props)

  // for category slider
  var cSlider = createRef();



  const classes = useStyles();
  const [categoryList, setCategoryList] = useState([])




  const fetchAllCategory = async () => {
    var result = await getData("getCategory");
    setCategoryList(result.data);
  };




  //category 
  const showCategories = () => {
    return categoryList.map((item) => {
      return (
      <div style={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center' }}>
        <div style={{ padding: 5, display: 'flex', justifyContent: 'center', alignItems: 'center', width: 170, height: 170, borderRadius: '50%', background: '#fff', margin: 10 }}>
          <img   alt="category" src={item.image} style={{ width: 120, Height: 120 }} />

        </div>
        <div style={{ textAlign: 'center', fontSize: 18, fontWeight: 'bold' }}>{item.categoryName}</div>

      </div>
      )
    })


  }






  useEffect(function () {
    fetchAllCategory()


  }, [])

  const handleBack = () => {
    cSlider.current.slickPrev()
  }
  const handleForward = () => {
    cSlider.current.slickNext()
  }


  return (

    <div className={classes.root}>
      <div style={{marginTop:50}}>

        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', flexDirection: 'row' }}>
          <ArrowBackIos onClick={() => handleBack()} />
          <div style={{ width: 1300, padding: 10 }}>
{/* 
            <Slider ref={cSlider}{...Categorysettings}>

              {showCategories()}

            </Slider> */}

            <div>{showCategories()}</div>

          </div>
          <ArrowForwardIos onClick={() => handleForward()} />
        </div>









      </div>

    </div>)


}
