import React,{useState} from 'react';
import { makeStyles } from '@mui/styles';
import {Grid,TextField,Button} from '@mui/material'
import {Save} from '@mui/icons-material';
import Swal from 'sweetalert2'
import { postDataAndImage } from '../FetchNodeServices';





const useStyles = makeStyles({
    root:{
       display:'flex',
       justifyContent:'center',
       alignItem:'center',
       paddingTop:100

    },
    subdiv:{

        display:'flex',
        justifyContent:'center',
        alignItem:'center',
        width:600,
        marginTop:20,
        padding:20,
        background:'#ecf0f1',
        borderRadius:5
  },
    inputstyle:{
        display:'none'
    },
    center:{
        display:'flex',
        justifyContent:'center',
        alignItems:'center',
    }
  });

export default function Login(props) {

    const [firstname, setfirstname] = useState("");
    const [lastname, setlastname] = useState("");
    const [username, setusername] = useState("");
    const [password,setpassword]=useState("");

    const addUser=async()=>{
        var formData = new FormData();
        formData.append('firstname',firstname);
        formData.append("lastname",lastname);
        formData.append("username",userName);
        formData.append("password",password);

    var result=await postDataAndImage('insertUser',formData) 

      if(result)
      {
      Swal.fire({

       text: 'User Added Succesfully',
       imageUrl: "/logo.jpg",

       imageAlt: 'Custom image',
       icon:'success'
     })
      }
      else
      {
       Swal.fire({

           text: 'Fail to add category',
           imageUrl: '/logo.jpg',

           imageAlt: 'Custom image',
           icon:'error'
         })  
      }




    }

    const classes = useStyles();

  return (
<div className={classes.root} >
        <div className={classes.subdiv}>
            <Grid container spacing={2}>
            <Grid item xs={12} >

               <Grid container spacing={1}> 
               <Grid item xs={12}>
                <div style={{display:'flex',flexDirection:'row',textAlign:'center'}}>
                    <div style={{fontSize:20,letterSpacing:1,fontWeight:800}}>
                        Sign In   
                    </div>
                  </div>
                  </Grid>

                </Grid>


                </Grid>

                <Grid item xs={12}>
                 <TextField onChange={(e)=>setfirstname(e.target.value)} fullWidth variant="outlined" label="Enter First Name"/>    
                </Grid>

                <Grid item xs={12}>
                 <TextField onChange={(e)=>setlastname(e.target.value)}  fullWidth variant="outlined" label="Enter Last Name"/>    
                </Grid>

                <Grid item xs={12}>
                 <TextField onChange={(e)=>setusername(e.target.value)}  fullWidth variant="outlined" label="Enter User Name"/>    
                </Grid>

                <Grid item xs={12}>
                 <TextField onChange={(e)=>setpassword(e.target.value)}  fullWidth variant="outlined" label="Enter  Password"/>    
                </Grid>




                <Grid item xs={12}>
               <Button onClick={addUser} fullWidth variant="contained" startIcon={<Save />}>Sign In</Button>
                    </Grid>


            </Grid>
        </div>

     </div>
    )
}
