import React,{useState} from 'react';
import { makeStyles } from '@mui/styles';
import {Grid,TextField,Button,Avatar} from '@mui/material'
import {Save,ClearAll,List} from '@mui/icons-material';
import Swal from 'sweetalert2'
import { postDataAndImage,ServerURL } from '../FetchNodeServices';
import MaterialTable from "material-table";




const useStyles = makeStyles({
    root:{
       display:'flex',
       justifyContent:'center',
       alignItem:'center',
       paddingTop:100

    },
    subdiv:{

        display:'flex',
        justifyContent:'center',
        alignItem:'center',
        width:800,
        marginTop:20,
        padding:20,
        background:'#ecf0f1',
        borderRadius:5
  },
    inputstyle:{
        display:'none'
    },
    center:{
        display:'flex',
        justifyContent:'center',
        alignItems:'center',
    }
  });
export default function Category() {
    const classes = useStyles();

    const [list, setList] = useState([]);



  return (
   <div className={classes.root} >
        <div className={classes.subdiv}>
            <Grid container spacing={2}>
            <Grid item xs={12} >

               <Grid container spacing={1}> 
               <Grid item xs={6}>
                <div style={{display:'flex',flexDirection:'row'}}>
                    <div style={{fontSize:18,letterSpacing:1,fontWeight:800}}>
                        Category List
                    </div>
                  </div>
                  </Grid>
                  {/* <Grid item xs={6} className={classes.center}>
                    <div>
                     <Button  startIcon={<List />} variant="contained">Category List</Button>   
                     </div>   
                   </Grid> */}
                </Grid>


                </Grid>

                <Grid>
                <MaterialTable
          title={
            <div
              style={{
                width: 700,
                display: "flex",
                flexDirection: "row",
                alignItems: "center",
              }}
            >
              <div style={{ padding: 5 }}>

              </div>

              <div
                style={{
                  marginLeft: 120,
                  fontSize: 20,
                  fontWeight: 700,
                  letterSpacing: 1,
                  padding: 5,
                }}
              >
                List of Categories
              </div>
            </div>
          }
          columns={[
            { title: "CategoryId", field: "categoryid" },
            { title: "Category Name", field: "categoryname" },
            {
              title: "Icon",
              field: "icon",
              render: (rowData) => (
                <Avatar
                  alt="Category"
                  variant="rounded"
                  src={`${ServerURL}/images/${rowData.image}`}
                  sx={{ width: 56, height: 56 }}
                />
              ),
            },
          ]}
          data={list}
          actions={[
            {
              icon: "edit",
              tooltip: "Edit Category",
              // onClick: (event, rowData) => handleClickOpen(rowData),
            },
            {
              icon: "delete",
              tooltip: "Delete Category",
              // onClick: (event, rowData) => handleDelete(rowData),
            },
          ]}
        />
                </Grid>




            </Grid>
        </div>

     </div>
  )
}
