                  onClick={() => handleClick()}
                  startIcon={<AddBox />}
                  variant="contained"
                >
                  New Category
                </Button>
              </div>

              <div
                style={{
                  marginLeft: 120,
                  fontSize: 20,
                  fontWeight: 700,
                  letterSpacing: 1,
                  padding: 5,
                }}
              >
                List of Categories
              </div>
            </div>
          }
          columns={[
            { title: "CategoryId", field: "categoryid" },
            { title: "Category Name", field: "categoryname" },
            {
              title: "Icon",
              field: "icon",
              render: (rowData) => (
                <Avatar
                  alt="Category"
                  variant="rounded"
                  src={`${ServerURL}/images/${rowData.icon}`}
                  sx={{ width: 56, height: 56 }}
                />
              ),
            },
          ]}
          data={list}
          actions={[
            {
              icon: "edit",
              tooltip: "Edit Category",
              onClick: (event, rowData) => handleClickOpen(rowData),
            },
            {
              icon: "delete",
              tooltip: "Delete Category",
              onClick: (event, rowData) => handleDelete(rowData),
            },
          ]}
        />
      </div>
      {showDialog()}
    </div>
  );
}
