import React, { useState, useEffect } from 'react';
import { styled, useTheme } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Drawer from '@mui/material/Drawer';
import CssBaseline from '@mui/material/CssBaseline';
import MuiAppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import List from '@mui/material/List';
import Divider from '@mui/material/Divider';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import InboxIcon from '@mui/icons-material/MoveToInbox';
import MailIcon from '@mui/icons-material/Mail';
import { getData, ServerURL } from '../FetchNodeServices';
import { Button } from '@mui/material';
import ShoppingCartSharpIcon from '@mui/icons-material/ShoppingCartSharp';
import Paper from '@mui/material/Paper';
import ShoppingCartComponent from './ShoppingCartComponent';
import { useSelector } from 'react-redux'
import Card from '@mui/material/Card';
import CardHeader from '@mui/material/CardHeader';
import CardMedia from '@mui/material/CardMedia';
import CardActions from '@mui/material/CardActions';
import { Typography } from '@mui/material';
import Grid from '@mui/material/Grid';
import SignIn from '../User interface/SignIn'
import { useNavigate } from "react-router-dom";
import Dashboard from '../Admin/Dashboard';

import {
  makeStyles,
} from "@material-ui/core";
import { useDispatch } from "react-redux";

const drawerWidth = 240;
const drawerWidthtwo = 500;



const Main = styled('main', { shouldForwardProp: (prop) => prop !== 'open' })(
  ({ theme, open }) => ({
    flexGrow: 1,
    padding: theme.spacing(3),
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    marginLeft: `-${drawerWidth}px`,
    ...(open && {
      transition: theme.transitions.create('margin', {
        easing: theme.transitions.easing.easeOut,
        duration: theme.transitions.duration.enteringScreen,
      }),
      marginLeft: 0,
    }),
  }),
);

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: 'center',
  color: theme.palette.text.secondary,
}));


const AppBar = styled(MuiAppBar, {
  shouldForwardProp: (prop) => prop !== 'open',
})(({ theme, open }) => ({
  transition: theme.transitions.create(['margin', 'width'], {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.leavingScreen,
  }),
  ...(open && {
    width: `calc(100% - ${drawerWidth}px)`,
    marginLeft: `${drawerWidth}px`,
    transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
  }),
}));

const DrawerHeader = styled('div')(({ theme }) => ({
  display: 'flex',
  alignItems: 'center',
  padding: theme.spacing(0, 1),
  // necessary for content to be below app bar
  ...theme.mixins.toolbar,
  justifyContent: 'flex-end',
}));

const useStyles = makeStyles((theme) => ({
  menuSliderContainer: {
    paddingTop: 65,
    width: 250,
    height: "100%"
  },
  avatar: {
    margin: "0.5rem auto",
    padding: "1rem",
    width: theme.spacing(13),
    height: theme.spacing(13)
  },
  listItem: {
    color: "tan"
  }
}));


export default function Home(props) {
  const theme = useTheme();
  const [open, setOpen] = React.useState(false);
  const [opent, setOpent] = React.useState(false);
  const [page_to_display, setPageToDisplay] = React.useState('Women');
  const [isLoaded, setIsLoaded] = React.useState(false);

  const [qty, setQty] = useState(0);
  var dispatch = useDispatch();

  const [categoryList, setCategoryList] = useState([]);
  const [all_products, setAllProducts] = useState([]);

  let navigate = useNavigate();
  const classes = useStyles();



  const navigateto =()=>{
    navigate('/dashboard')
  }

  const navigatetoProceed =()=>{
    navigate('/signin')
  }

  const handleDrawerToggle = () => {
    setOpen(!open);
  };

  const toggleSlider = () => {
    setOpent(!opent);
  };

  const fetchAllItem = async () => {
    var result = await getData("getProductbyId");
    console.log("RESULT", result);
    setCategoryList(Object.keys(result));
    setAllProducts(result);
    setIsLoaded(true);
  };

  useEffect(function () {
      fetchAllItem();

    // fetchAllProduct()
    // fetchAllProductbyId()

  }, []);

  const handleQtyChange = (value, item) => {

    if (value > 0) {
      item["qty"] = value;

      setQty(value);
      dispatch({ type: "ADD_ITEM", payload: [item.productid, item] });
    } else {
      dispatch({ type: "REMOVE_ITEM", payload: [item.productid, item] });
    }
  };

  var cart = useSelector(state => state.cart)
  var cartitems = Object.values(cart);


  var totalamount = cartitems.reduce((a, b) => getTotalAmount(a, b), 0)
  function getTotalAmount(p1, p2) {
    var price = p2.offerprice > 0 ? p2.offerprice * p2.qty : p2.price * p2.qty
    return (price + p1)
  }


  const sideList = () => (
    <Box sx={{width:drawerWidthtwo}} className={classes.menuSliderContainer} component="div">



      <List>
      {cartitems.map((item, index) => (
        <ListItem button >
          <ListItemIcon>
           <img src={item.image} style={{width:80,borderRadius:10}} />
          </ListItemIcon>
          <div style={{display:'flex',flexDirection:'column',padding:5}}>
          <ListItemText primary={item.productname}  style={{fontWeight:'bold'}} />

          <ListItemText primary= {item.offerprice > 0 ? (
                <div
                  style={{width:280, fontSize: 18, fontWeight: "500", letterSpacing: 1 }}
                >
                  <s
                    style={{
                      color: "#353b48",
                      fontSize: 14,
                      fontWeight: "500",
                      letterSpacing: 1,
                    }}
                  >
                    &#8377; {item.price}  
                  </s>{" "}


                  &#8377;{item.offerprice} x {item.qty}

                </div>
              ) : (
                <>
                  <div
                    style={{
                      width:280,
                      fontSize: 18,
                      fontWeight: "bold",
                      letterSpacing: 2,
                    }}
                  >
                    &#8377;{item.price} x {item.qty}
                  </div>
                  <div
                    style={{
                      display:'flex',
                      color: "darkgreen",
                      fontSize: 18,
                      fontWeight: "500",
                      letterSpacing: 1,
                    }}
                  >
                    &nbsp;
                    <span style={{marginLeft:'auto'}}>
                   &#8377;{item.price * item.qty}
                   </span>
                  </div>


                </>
              )}  />

           </div>


        </ListItem>
      ))}
    </List>

    <Divider />

  <div style={{padding:10,display:'flex',alignItems:'center',width:380}}>
    <span style={{fontWeight:'bold',fontSize:18}}>Net Amount:</span>
    <span style={{fontWeight:'bold',fontSize:18,marginLeft:'auto'}}>&#8377;{totalamount}
</span> 
  </div>

  <div style={{padding:10,display:'flex',alignItems:'center',width:380}}>
    <Button  onClick={()=>navigatetoProceed()} variant="contained" fullWidth style={{background:'black',color:'#FFF',fontWeight:'bold',fontSize:18}}>Proceed</Button>
  </div>
    </Box>
  );

  const showCategories = () => {
    return categoryList.map((item) => {
      return (
        <div style={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center' }}>
          <br /> <Button onClick={() => setPageToDisplay(item)} fullWidth style={{ textAlign: 'center', fontSize: 18, fontWeight: 'bold' }}>{item}</Button>
          <br />
        </div>
      )
    })
  }

  const showProduct = (items) => {
    return items.map((item) => {

      return (
        <div style={{  display: 'flex',
  flexFlow:'column wrap' }} >
          <Card sx={{ maxWidth: 250 }}>
            <CardHeader style={{ textAlign: 'center' }}
              title={item.productName} />
            {/* <img src={item.image}/> */}
            <CardMedia
              component="img"
              height="194"
              image={item.image}

            />
            <Typography style={{ textAlign: 'center', marginTop: 10 }}>Price : {item.price}</Typography>
            <Typography style={{ textAlign: 'center', marginTop: 10 }}> Description : {item.productDescription}</Typography>
            <CardActions style={{ justifyContent: 'center' }} disableSpacing>
              <ShoppingCartComponent
                // item = {...item}
                value={qty}
                onChange={(value) => handleQtyChange(value, item)}
              />
            </CardActions>
          </Card>
          <br />
          <br />
        </div>
      )
    })
  }

  if(!isLoaded)
    return (<div>Loading...</div>)

  return (
    <Box sx={{ display: 'flex' }}>
      <CssBaseline />
      <AppBar position="fixed" open={open}>
        <Toolbar>
          <IconButton
            color="inherit"
            aria-label="open drawer"
            onClick={handleDrawerToggle}
            edge="start"
            sx={{ mr: 2, ...(open && { display: 'none' }) }}
          >
            <MenuIcon />
          </IconButton>
          <Typography variant="h6" noWrap component="div">
            List of Products
          </Typography>

          <Button style={{
            position: 'absolute',
            left: '80%',
            bottom: '20%',
            color: 'white'
          }}
            onClick={navigateto}
          >
            Admin Pannel
          </Button>

          <ShoppingCartSharpIcon style={{
            position: 'absolute',
            left: '95%',
            bottom: '30%'
          }} onClick={toggleSlider}>

          </ShoppingCartSharpIcon>
          <Drawer open={opent} anchor="right" onClose={toggleSlider}>
            {sideList()}
          </Drawer>
        </Toolbar>
      </AppBar>
      <Drawer
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          '& .MuiDrawer-paper': {
            width: drawerWidth,
            boxSizing: 'border-box',
          },
        }}
        variant="persistent"
        anchor="left"
        open={open}
      >
        <DrawerHeader>
          <IconButton onClick={handleDrawerToggle}>
            {theme.direction === 'ltr' ? <ChevronLeftIcon /> : <ChevronRightIcon />}
          </IconButton>
        </DrawerHeader>
        {/* <Divider /> */}

        <List>
          {showCategories()}
          <Divider />
        </List>

      </Drawer>
      <Main open={open}>
        <DrawerHeader />
        {showProduct([...all_products[page_to_display]])}
      </Main>
    </Box>
  );
}
