import React,{useState} from 'react';
import { makeStyles } from '@mui/styles';
import {Grid,TextField,Button,Avatar} from '@mui/material'
import {Save,ClearAll,List} from '@mui/icons-material';
// import Swal from 'sweetalert2'
import Axios from "axios";





const useStyles = makeStyles({
    root:{
       display:'flex',
       justifyContent:'center',
       alignItem:'center',
       paddingTop:100

    },
    subdiv:{

        display:'flex',
        justifyContent:'center',
        alignItem:'center',
        width:800,
        marginTop:20,
        padding:20,
        background:'#ecf0f1',
        borderRadius:5
  },
    inputstyle:{
        display:'none'
    },
    center:{
        display:'flex',
        justifyContent:'center',
        alignItems:'center',
    }
  });
export default function Category() {
    const classes = useStyles();

    const [firstname, setfirstname] = useState("");
    const [lastname, setlastname] = useState("");
    const [username, setusername] = useState("");
    const [password, setpassword] = useState("");
    const [picture,setPicture]=useState({filename:"/cam.png",bytes:""})
    const [employeeList, setEmployeeList] = useState([]);

    const addCategory = () => {
      Axios.post("http://localhost:3001/insert", {
        firstname: firstname,
        lastname: lastname,
        username: username,
        password: password,
      }).then(() => {
        setEmployeeList([
          ...employeeList,
          {
            firstname: firstname,
            lastname: lastname,
            username: username,
            password: password,
          },
        ]);
      });
    };


  return (
   <div className={classes.root} >
        <div className={classes.subdiv}>
            <Grid container spacing={2}>
            <Grid item xs={12} >

               <Grid container spacing={1}> 
               <Grid item xs={6}>
                <div style={{display:'flex',flexDirection:'row'}}>
                    <div style={{fontSize:18,letterSpacing:1,fontWeight:800}}>
                        Category  
                    </div>
                  </div>
                  </Grid>
                  {/* <Grid item xs={6} className={classes.center}>
                    <div>
                     <Button  startIcon={<List />} variant="contained">Category List</Button>   
                     </div>   
                   </Grid> */}
                </Grid>


                </Grid>

                <Grid item xs={12}>
                 <TextField  fullWidth variant="outlined" onChange={(event)=> setfirstname(event.target.value)} label="Enter your First Name"/>    
                </Grid>
                <Grid item xs={12}>
                 <TextField  fullWidth variant="outlined" onChange={(event)=> setlastname(event.target.value)} label="Enter your Last Name"/>    
                </Grid>
                <Grid item xs={12}>
                 <TextField  fullWidth variant="outlined" onChange={(event)=> setusername(event.target.value)} label="Enter your Username"/>    
                </Grid>
                <Grid item xs={12}>
                 <TextField  fullWidth variant="outlined" onChange={(event)=> setpassword(event.target.value)} label="Enter your Password"/>    
                </Grid>
                <Grid item xs={6}>
                <label htmlFor="contained-button-file">
        <input  className={classes.inputstyle} accept="image/*" id="contained-button-file" multiple type="file" />
        <Button  fullWidth variant="contained" component="span">
          Upload Image
        </Button>
      </label>    
                </Grid>
            <Grid item xs={6} className={classes.center} >

            <Avatar alt="Picture" variant="rounded"  />
                </Grid>


                <Grid item xs={12}>
               <Button fullWidth onClick={addCategory} variant="contained" startIcon={<Save />}>Save</Button>
                    </Grid>
                    {/* <Grid item xs={6}>
               <Button fullWidth variant="contained" startIcon={<ClearAll />}>Reset</Button>
                    </Grid> */}

            </Grid>
        </div>

     </div>
  )
}
