import Box from "@mui/material/Box";
import Drawer from "@mui/material/Drawer";
import AppBar from "@mui/material/AppBar";
import CssBaseline from "@mui/material/CssBaseline";
import Toolbar from "@mui/material/Toolbar";
import List from "@mui/material/List";
import Typography from "@mui/material/Typography";
import ListItem from "@mui/material/ListItem";
import ListItemButton from "@mui/material/ListItemButton";
import ListItemIcon from "@mui/material/ListItemIcon";
import ListItemText from "@mui/material/ListItemText";
import Veg from "./Veg.js";
import NonVeg from "./NonVeg";
import { FoodBank } from "@mui/icons-material";
import ShoppingCartSharpIcon from '@mui/icons-material/ShoppingCartSharp';
import React, { useState } from "react";
import Category from "../Admin/Category.js";
import {
  Divider,
  makeStyles,
} from "@material-ui/core";
import {
  Apps,
  Menu,
  ContactMail,
  AssignmentInd,
  Home
} from "@material-ui/icons";

const useStyles = makeStyles((theme) => ({
  menuSliderContainer: {
    paddingTop:65,
    width: 250,
    height: "100%"
  },
  avatar: {
    margin: "0.5rem auto",
    padding: "1rem",
    width: theme.spacing(13),
    height: theme.spacing(13)
  },
  listItem: {
    color: "tan"
  }
}));



const drawerWidth = 240;

const App = () => {
  const [page1, setPage1] = React.useState("");

  const setThePage = (page) => {
    console.log("HEY", page);
    setPage1(page);
  };

  const [list, setList] = useState([]);

  const fetchAllCategory = async () => {

    var result = await getData("getCategory");
    setList(result.data);
    console.log(result.data)

  };


  const classes = useStyles();
  const [open, setOpen] = useState(false);

  const toggleSlider = () => {
    setOpen(!open);
  };

  const sideList = () => (
    <Box className={classes.menuSliderContainer} component="div">

      <Divider />
      <List>
        {/* {listItems.map((listItem, index) => (
          <ListItem className={classes.listItem} button key={index}>
            <ListItemIcon className={classes.listItem}>
              {listItem.listIcon}
            </ListItemIcon>
            <ListItemText primary={listItem.listText} />
          </ListItem>
        ))} */}
      </List>
    </Box>
  );

  return (
    <Box sx={{ display: "flex" }}>
      <CssBaseline />
      <AppBar
        position="fixed"
        sx={{ zIndex: (theme) => theme.zIndex.drawer + 1 }}
      >
        <Toolbar>
          <Typography variant="h6" noWrap component="div">
            Food Menu
          </Typography>
          <ShoppingCartSharpIcon style={{position:'absolute',
left:'95%',
bottom:'30%'}} onClick={toggleSlider}>
              <Menu />
            </ShoppingCartSharpIcon>
            <Drawer open={open} anchor="right" onClose={toggleSlider}>
              {sideList()}
            </Drawer>
        </Toolbar>
      </AppBar>
      <Drawer
        variant="permanent"
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          [`& .MuiDrawer-paper`]: {
            width: drawerWidth,
            boxSizing: "border-box"
          }
        }}
      >
        <Toolbar />
        <Box sx={{ overflow: "auto" }}>
          <List>
            {["Veg", "Non Veg"].map((text, index) => (
              <ListItem
                key={text}
                onClick={() => {
                  setThePage(text);
                }}
              >
                <ListItemButton>
                  <ListItemIcon>
                    {index % 2 === 0 ? <FoodBank /> : <FoodBank />}
                  </ListItemIcon>
                  <ListItemText primary={text} />
                </ListItemButton>
              </ListItem>
            ))}
          </List>
          {/* <Divider /> */}

        </Box>
      </Drawer>
      <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
        <Toolbar />
        {page1 === "Veg" ? 
        (
          <Veg   />
        ) 
        : 
        page1 === "Non Veg" ? (
          <NonVeg />
        ) : (
          <span>Make more pages</span>
        )}
      </Box>
    </Box>
  );
};

export default App;
