import React,{useState,useEffect} from 'react';
import { makeStyles } from '@mui/styles';
import {Grid} from '@mui/material'


import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import { getData } from '../FetchNodeServices';


const useStyles = makeStyles({
    root:{
       display:'flex',
       justifyContent:'center',
       alignItem:'center',
       paddingTop:100

    },
    subdiv:{

        display:'flex',
        justifyContent:'center',
        alignItem:'center',
        width:800,
        marginTop:20,
        padding:20,
        background:'#ecf0f1',
        borderRadius:5
  },
    inputstyle:{
        display:'none'
    },
    center:{
        display:'flex',
        justifyContent:'center',
        alignItems:'center',
    }
  });
export default function Category() {
    const classes = useStyles();

    const [list, setList] = useState([]);


  const fetchAllCategory = async () => {
    var result = await getData("getCategory");
    setList(result.data);
  };

  useEffect(function () {
    fetchAllCategory();
  }, []);




  return (
   <div className={classes.root} >
        <div className={classes.subdiv}>
            <Grid container spacing={2}>
            <Grid item xs={12} >

               <Grid container spacing={1}> 
               <Grid item xs={6}>
                <div style={{display:'flex',flexDirection:'row'}}>
                    <div style={{fontSize:18,letterSpacing:1,fontWeight:800}}>
                        Category List
                    </div>
                  </div>
                  </Grid>
                  {/* <Grid item xs={6} className={classes.center}>
                    <div>
                     <Button  startIcon={<List />} variant="contained">Category List</Button>   
                     </div>   
                   </Grid> */}
                </Grid>


                </Grid>

                <Grid>

                <TableContainer component={Paper}>
      <Table sx={{ minWidth: 650 }} aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell align="left">Category Name </TableCell>
            <TableCell align="left">Image</TableCell>

          </TableRow>
        </TableHead>
        <TableBody>
          {list.map((item) => (
            <TableRow
              key={item.id}
              sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
            >

              <TableCell align="left">{item.categoryName}</TableCell>
              <TableCell align="left"><img style={{width:200}} src={item.image}/></TableCell>

            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>

                </Grid>




            </Grid>
        </div>

     </div>
  )
}
