import  React,{useState} from 'react';
import Card from '@mui/material/Card';
import CardHeader from '@mui/material/CardHeader';
import CardMedia from '@mui/material/CardMedia';
import CardActions from '@mui/material/CardActions';
import { Typography } from '@mui/material';



export default function NonVeg() {

    const veg = [
        {
            id: 1,
            title: 'Rajma',
            price:150,
            quantity:0
        },
        {
            id: 2,
            title: 'Aaloo Gobi',
            price:150,
            quantity:0


        },
        {
            id: 3,
            title: 'Chole',
            price:150,
            quantity:0


        },
        {
            id: 4,
            title: 'Paneer',
            price:150,
            quantity:0


        },
        {
            id: 5,
            title: 'Daal Tadka',
            price:150,
            quantity:0


        },
        {
            id: 6,
            title: 'Daa Makhani',
            price:150,
            quantity:0


        }

    ]

    const [count, setCount] = useState(0);

    const countManagement = (sign) => {
      let val = count + (sign?1:-1);
      if(val<0)
        return;
      setCount(val);
    };




    return (
        <div  >

            {
                veg.map((item, key) => {
                    return (
                   <div   >
                         <Card style={{}}  key={key}  sx={{ maxWidth: 250 }}>
                            <CardHeader style={{textAlign:'center'}} 
                                title={item.title} />
                            {/* <img src={item.image}/> */}
                            <CardMedia
                                component="img"
                                height="194"
                                image={item.image}

                            />
                            <Typography style={{textAlign:'center',marginTop:10}}>Price : {item.price}</Typography>
                            <CardActions style={{justifyContent:'center'}} disableSpacing>
                            Quantity : &nbsp;
      <button style={{color:"red"}} onClick={()=>countManagement(0)}>-</button>
      &nbsp;
      {count}
      &nbsp;
    <button style={{color:"green"}} onClick={()=>countManagement(1)}>+</button>

                            </CardActions>

                        </Card>
                        <br/>
                    </div>

                    )
                })
            }






        </div>
    );
}
