import React, { useState, useEffect } from 'react';
import { styled, useTheme } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Drawer from '@mui/material/Drawer';
import CssBaseline from '@mui/material/CssBaseline';
import MuiAppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import List from '@mui/material/List';
import Typography from '@mui/material/Typography';
import Divider from '@mui/material/Divider';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import InboxIcon from '@mui/icons-material/MoveToInbox';
import MailIcon from '@mui/icons-material/Mail';
import { getData } from '../FetchNodeServices';
import { Button } from '@mui/material';
import ShoppingCartSharpIcon from '@mui/icons-material/ShoppingCartSharp';
import Paper from '@mui/material/Paper';
import {
  makeStyles,
} from "@material-ui/core";

const drawerWidth = 240;
const drawerWidthTwo = 240;


const Main = styled('main', { shouldForwardProp: (prop) => prop !== 'open' })(
  ({ theme, open }) => ({
    flexGrow: 1,
    padding: theme.spacing(3),
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    marginLeft: `-${drawerWidth}px`,
    ...(open && {
      transition: theme.transitions.create('margin', {
        easing: theme.transitions.easing.easeOut,
        duration: theme.transitions.duration.enteringScreen,
      }),
      marginLeft: 0,
    }),
  }),
);

const AppBar = styled(MuiAppBar, {
  shouldForwardProp: (prop) => prop !== 'open',
})(({ theme, open }) => ({
  transition: theme.transitions.create(['margin', 'width'], {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.leavingScreen,
  }),
  ...(open && {
    width: `calc(100% - ${drawerWidth}px)`,
    marginLeft: `${drawerWidth}px`,
    transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
  }),
}));

const DrawerHeader = styled('div')(({ theme }) => ({
  display: 'flex',
  alignItems: 'center',
  padding: theme.spacing(0, 1),
  // necessary for content to be below app bar
  ...theme.mixins.toolbar,
  justifyContent: 'flex-end',
}));

const useStyles = makeStyles((theme) => ({
  menuSliderContainer: {
    paddingTop: 65,
    width: 250,
    height: "100%"
  },
  avatar: {
    margin: "0.5rem auto",
    padding: "1rem",
    width: theme.spacing(13),
    height: theme.spacing(13)
  },
  listItem: {
    color: "tan"
  }
}));


export default function Home() {
  const theme = useTheme();
  const [open, setOpen] = React.useState(false);
  const [opent, setOpent] = React.useState(false);


  const handleDrawerOpen = () => {
    setOpen(true);
  };
  const classes = useStyles();

  const sideList = () => (
    <Box className={classes.menuSliderContainer} component="div">
      <Divider />
      <List>

      </List>
    </Box>
  );


  const handleDrawerClose = () => {
    setOpen(false);
  };
  const toggleSlider = () => {
    setOpent(!opent);
  };


  const [categoryList, setCategoryList] = useState([])
  const fetchAllCategory = async () => {
    var result = await getData("getCategory");
    setCategoryList(result.data);
    console.log(result.data)
  };
  useEffect(function () {
    fetchAllCategory()

  }, [])

  const [productList, setProductList] = useState([])
  const fetchAllProduct = async () => {
    var result = await getData("getProduct");
    setProductList(result.data);
    console.log(result.data)
  };
  useEffect(function () {
    fetchAllProduct()

  }, [])



  const showCategories = () => {
    return categoryList.map((item) => {
      return (
        <div style={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center' }}>
          <br />        <Button  fullWidth style={{ textAlign: 'center', fontSize: 18, fontWeight: 'bold' }}>{item.categoryName}</Button>
          <br />
        </div>
      )
    })
  }

  const showProduct = () => {
    return productList.map((item) => {
      return (
        <div
          style={{
            fontSize: 36,
            fontWeight: "bold",
            letterSpacing: 1,
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            flexDirection: "column",
            padding: 2,
          }}
        >
          <Paper
            style={{
              display: "flex",
              flexDirection: "column",
              justifyContent: "center",
              alignItems: "center",
              width: 100,
              height: 100,
              background: "pink",
              margin: 10,
              padding: 20,
            }}
            elevation={2}
          >
            <div
              style={{
                textAlign: "center",
                fontSize: 18,
                fontWeight: "600",
                letterSpacing: 2,
                padding: 5,
              }}
            >
              {/* {item.companyname} {item.productname} */}{item.productName}
            </div>

            <div
              style={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                backgroundColor:'pink'
              }}
            >
              <img   
                src={item.image}
                style={{ width: 100, Height: 100 }}
              />
            </div>

            <div
              style={{
                padding: 5,
                textAlign: "center",
                fontSize: 18,
                fontWeight: "400",
                letterSpacing: 2,
              }}
            >
              {/* <div>{item.model}</div> */} {item.productDescription}
            </div>
            <div style={{ textAlign: "center" }}>


                  <div
                    style={{
                      fontSize: 18,
                      fontWeight: "bold",
                      letterSpacing: 2,
                    }}
                  >
                    {/* &#8377;{item.price}{" "} */}{item.price}
                  </div>{" "}

            </div>
            <div sx={{ padding: 5 }}>
              {/* <ShoppingCartComponent
                value={qty}
                onChange={(value) => handleQtyChange(value, item)}
              /> */}
            </div>
          </Paper>
        </div>
      )
    })
  }



  return (
    <Box sx={{ display: 'flex' }}>
      <CssBaseline />
      <AppBar position="fixed" open={open}>
        <Toolbar>
          <IconButton
            color="inherit"
            aria-label="open drawer"
            onClick={handleDrawerOpen}
            edge="start"
            sx={{ mr: 2, ...(open && { display: 'none' }) }}
          >
            <MenuIcon />
          </IconButton>
          <Typography variant="h6" noWrap component="div">
            List of Products
          </Typography>

          <ShoppingCartSharpIcon style={{
            position: 'absolute',
            left: '95%',
            bottom: '30%'
          }} onClick={toggleSlider}>

          </ShoppingCartSharpIcon>
          <Drawer open={opent} anchor="right" onClose={toggleSlider}>
            {sideList()}
          </Drawer>
        </Toolbar>
      </AppBar>
      <Drawer
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          '& .MuiDrawer-paper': {
            width: drawerWidth,
            boxSizing: 'border-box',
          },
        }}
        variant="persistent"
        anchor="left"
        open={open}
      >
        <DrawerHeader>
          <IconButton onClick={handleDrawerClose}>
            {theme.direction === 'ltr' ? <ChevronLeftIcon /> : <ChevronRightIcon />}
          </IconButton>
        </DrawerHeader>
        <Divider />

        <List>
          {showCategories()}
          <Divider />
        </List>

      </Drawer>
      <Main open={open}>
        <DrawerHeader />
        {/* <Typography paragraph>
          Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod
          tempor incididunt ut labore et dolore magna aliqua. Rhoncus dolor purus non
          enim praesent elementum facilisis leo vel. Risus at ultrices mi tempus
          imperdiet. Semper risus in hendrerit gravida rutrum quisque non tellus.
          Convallis convallis tellus id interdum velit laoreet id donec ultrices.
          Odio morbi quis commodo odio aenean sed adipiscing. Amet nisl suscipit
          adipiscing bibendum est ultricies integer quis. Cursus euismod quis viverra
          nibh cras. Metus vulputate eu scelerisque felis imperdiet proin fermentum
          leo. Mauris commodo quis imperdiet massa tincidunt. Cras tincidunt lobortis
          feugiat vivamus at augue. At augue eget arcu dictum varius duis at
          consectetur lorem. Velit sed ullamcorper morbi tincidunt. Lorem donec massa
          sapien faucibus et molestie ac.
        </Typography> */}


{showProduct()}
      </Main>
    </Box>
  );
}
