import React, { useEffect, useState } from "react";
import Header from "./Header";
import { postData, ServerURL } from "../FetchNodeServices";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";
import Paper from "@mui/material/Paper";
import { makeStyles } from "@mui/styles";
import { useDispatch } from "react-redux";
import Footer from "./Footer";
import ShoppingCartComponent from "./ShoppingCartComponent";
import SideBar from "./SideBar";




var settings = {
  dots: false,
  infinite: true,
  speed: 1000,
  autoplay: true,
  slidesToShow: 1,
  slidesToScroll: 1,
  autoplaySpeed: 3000,
};

const useStyles = makeStyles({
  root: {
    background: "#ecf0f1",
  },
});



export default function ProductList(props) {
  console.log(props);
  const classes = useStyles();
  var dispatch = useDispatch();


  const [listProducts, setListProducts] = useState([]);
  const [listSubcategoryBanner, setSubCategoryBannerList] = useState([]);
  const [qty, setQty] = useState(0);
  const [refresh, setRefresh] = useState(false);


  const fetchAllSubCategoryById = async () => {
    var body = { subcategoryid: props.location.state.subcategoryid };
    var result = await postData("subbanner/displayallbannerbyid", body);
    setSubCategoryBannerList(result.data);
  };

  const fetchAllProductList = async () => {
    var body = { subcategoryid: props.location.state.subcategoryid };
    var result = await postData(
      "finalproduct/displayallfinalproductbysubcategoryid",
      body
    );
    setListProducts(result.data);
  };
  const handleQtyChange = (value,item) => {

    if (value > 0) {
      item["qty"] = value;

      setQty(value);
      dispatch({ type: "ADD_ITEM", payload: [item.finalproductid, item] });
    } else {
      dispatch({ type: "REMOVE_ITEM", payload: [item.finalproductid, item] });
    }
    setRefresh(!refresh);
  };

  const showProducts = () => {
    return listProducts.map((item) => {
      return (
        <div
          style={{
            fontSize: 36,
            fontWeight: "bold",
            letterSpacing: 1,
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            flexDirection: "column",
            padding: 2,
          }}
        >
          <Paper
            style={{
              display: "flex",
              flexDirection: "column",
              justifyContent: "center",
              alignItems: "center",
              width: 320,
              height: 360,
              background: "#fff",
              margin: 10,
              padding: 20,
            }}
            elevation={2}
          >
            <div
              style={{
                textAlign: "center",
                fontSize: 18,
                fontWeight: "600",
                letterSpacing: 2,
                padding: 5,
              }}
            >
              {item.companyname} {item.productname}
            </div>

            <div
              style={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
              }}
            >
              <img  alt="category" 
                src={`${ServerURL}/images/${item.picture}`}
                style={{ width: 170, Height: 170 }}
              />
            </div>

            <div
              style={{
                padding: 5,
                textAlign: "center",
                fontSize: 18,
                fontWeight: "400",
                letterSpacing: 2,
              }}
            >
              <div>{item.model}</div>
            </div>
            <div style={{ textAlign: "center" }}>
              {item.offerprice > 0 ? (
                <div
                  style={{ fontSize: 18, fontWeight: "500", letterSpacing: 1 }}
                >
                  &#8377;{item.offerprice}{" "}
                  <s
                    style={{
                      color: "#353b48",
                      fontSize: 14,
                      fontWeight: "500",
                      letterSpacing: 1,
                    }}
                  >
                    &#8377; {item.price}
                  </s>{" "}
                  <div
                    style={{
                      color: "darkgreen",
                      fontSize: 18,
                      fontWeight: "500",
                      letterSpacing: 1,
                    }}
                  >
                    You Save &#8377; {item.price - item.offerprice}{" "}
                  </div>{" "}
                </div>
              ) : (
                <>
                  <div
                    style={{
                      fontSize: 18,
                      fontWeight: "bold",
                      letterSpacing: 2,
                    }}
                  >
                    &#8377;{item.price}{" "}
                  </div>{" "}
                  <div
                    style={{
                      color: "darkgreen",
                      fontSize: 18,
                      fontWeight: "500",
                      letterSpacing: 1,
                    }}
                  >
                    &nbsp;
                  </div>
                </>
              )}
            </div>
            <div sx={{ padding: 5 }}>
              <ShoppingCartComponent
                value={qty}
                onChange={(value) => handleQtyChange(value, item)}
              />
            </div>
          </Paper>
        </div>
      );
    });
  };
  const showSlider = () => {
    return listSubcategoryBanner.map((item) => {
      return (
        <div>
          <img  alt="category" 
            src={`${ServerURL}/images/${item.image}`}
            style={{ width: "100%", Height: 200 }}
          />
        </div>
      );
    });
  };

  useEffect(function () {
    fetchAllProductList();
    fetchAllSubCategoryById();
  }, [fetchAllProductList,fetchAllProductList]);
  return (
    <div className={classes.root}>
      <Header history={props.history} />

      <Slider {...settings}>{showSlider()}</Slider>

      <div style={{ display: "flex", flexDirection: "row", marginTop:10}}>
        <div style={{ padding: 10 }}>
          <SideBar />
        </div>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            flexDirection: "row",
            padding: 10,
            flexWrap: "wrap",
          }}
        >
          {showProducts()}
        </div>
      </div>
      <Footer />




    </div>
  );
}
