import React,{useState} from "react";



import {Button,Avatar} from '@mui/material';

import {ShoppingCartOutlined} from '@mui/icons-material';

// const useStyles = makeStyles({
//   rounddiv: {
//     border:'2px solid #000',
//     width:30,
//     height:30,
//     borderRadius:15,
//     display:'flex',
//     justifyContent:'center',
//     alignItems:'center',
//     fontWeight:'bold',
//     fontSize:18,
//   },
// })
export default function ShoppingCartComponent(props){
  console.log("PROPS");

  // const classes = useStyles();
  const [value,setValue]=useState(props.value);
  console.log("value", value);


  const handleMinus=()=>{

    var c=value-1
    if(c>=0)
     setValue(c)
     props.onChange(c)

  }

  const handlePlus=()=>{
    var c=value+1
    setValue(c)
    props.onChange(c)


}
const handleClick=()=>{
    var c=value+1
    setValue(c)
    props.onChange(c)


}
 return(
<div>
{value===0?
<div><Button onClick={()=>handleClick()} sx={{margin:3,width:180,background:'grey',color:'black'}}  variant="contained" endIcon={<ShoppingCartOutlined />}>
Add to cart
</Button></div>:

   <div style={{display:'flex',flexDirection:'row',alignItems:'center'}}>

 <Avatar onClick={()=>handleMinus()} sx={{ bgcolor:'grey',color:'black',fontSize:15,fontWeight:'bold',margin:3}} variant="square">
  -
  </Avatar>

   <div  sx={{display:'flex',fontSize:15,fontWeight:'bold'}}>{value}</div>

   <Avatar onClick={()=>handlePlus()} sx={{ bgcolor: 'grey',color:'black',fontSize:15,fontWeight:'bold',margin:3 }} variant="square">
  +
  </Avatar>

   </div>
   }
</div>
 )

}
